import React, { Component } from 'react';
// import { iex } from '../iex.js';
import { stock } from '../stock.js'

const changeStyle = {
    color: '#4caf50',
    fontSize: '0.8rem',
    marginLeft: 5
}

class StockRow extends Component {

    constructor(props) {
        super(props)
        this.state = {
            data: {
                dollar_change: 0,
                percent_change: 0
            }
        }
    }

    applyData(data) {
        console.log(data)
        this.setState({ data: data })
    }

    componentDidMount() {
        /*
        const url = `${iex.base_url}/${this.props.ticker}/intraday-prices?token=${iex.api_token}&chartLast=1`
        // https://cloud.iexapis.com/stable/stock/aapl/intraday-prices?token=pk_12ff756a55f3487285968715b4e7e7d1&chartLast=20

        fetch(url)
            .then((response) => response.json())
        */
        stock.latestPrice(this.props.ticker, this.applyData.bind(this))
    }

    render() {
        return (
            <li className="list-group-item">
                <b>{this.props.name} - {this.props.ticker}</b> ${this.state.data.price}
                <span className="change" style={changeStyle}>
                    {this.state.data.dollar_change}
                    {this.state.data.percent_change}
                    +10.55 (5.0%)
                </span>
            </li>
        )
    }

}

export default StockRow;

