import { iex } from './iex.js';

export const stock = {

    latestPrice: (ticker, callback) => {
        fetch(stock.latestPriceURL(ticker))
        .then((response) => response.json())
        .then((data) => callback(stock.formatPriceData(data)))
    },

    latestPriceURL: (ticker) => {
        return `${iex.base_url}/${ticker}/intraday-prices?token=${iex.api_token}&chartLast=1`
        // &exactDate=20200512
    },

    formatPriceData: (data) => {
        const stockData = data[data.length - 1]
        const formattedData = {}
        formattedData.price = stockData.close
        formattedData.date = stockData.date
        formattedData.time = stockData.label
        formattedData.numberOfTrades = stockData.numberOfTrades
        return formattedData
    },

    getYesterdaysClose: (ticker, date, callback) => {
        fetch(stock.yesterdaysCloseURL(ticker, date))
        .then((response) => response.json())
        .then((data) => callback(stock.formatPriceData(data))) 
    },

    yesterdaysCloseURL: (ticker, date) => {
        var today = new Date(date).toISOString().split('T')[0].replace()
        return `${iex.base_url}/${ticker}/intraday-prices?token=${iex.api_token}&exactDate=20200511&chartLast=1`
    }

}
