import React, { Component } from 'react';
// import { iex } from '../iex.js';
import { stock } from '../stock.js'

const changeStyle = {
    color: '#4caf50',
    fontSize: '0.8rem',
    marginLeft: 5
}

class StockRow extends Component {

    constructor(props) {
        super(props)
        this.state = {
            price: null,
            date: null,
            time: null,
            numberOfTrades: null,
            dollar_change: null,
            percent_change: null,
        }
    }

    applyData(data) {
        console.log(data)
        this.setState({
            price: data.price.toXF,
            date: data.date,
            time: data.time,
            numberOfTrades: data.numberOfTrades,
        });
        stock.getYesterdaysClose(this.props.ticker, data.date, (yesterday) => {
            console.log(this.props.ticker, yesterday)
            const dollar_change = (data.price - yesterday.price).toFixed(3);
            this.setState({
                dollar_change: dollar_change,
                percent_change: (100 * dollar_change / yesterday.price)
            })
        })
    }

    componentDidMount() {
        /*
        const url = `${iex.base_url}/${this.props.ticker}/intraday-prices?token=${iex.api_token}&chartLast=1`
        // https://cloud.iexapis.com/stable/stock/aapl/intraday-prices?token=pk_12ff756a55f3487285968715b4e7e7d1&chartLast=20

        fetch(url)
            .then((response) => response.json())
        */
        stock.latestPrice(this.props.ticker, this.applyData.bind(this))
    }

    render() {
        return (
            <li className="list-group-item">
                <b>{this.props.name} - {this.props.ticker}</b> ${this.state.price}
                <span className="change" style={changeStyle}>
                    {this.state.dollar_change}
                    &nbsp; &nbsp;
                    {this.state.percent_change}
                </span>
            </li>
        )
    }

}

export default StockRow;

